mapper.select:
SELECT rental_order.id, rental_order.orderNo, rental_order.userId, user.userName, rental_order.vehicleId, vehicle.name AS vehicleName, vehicle.imageUrl AS vehicleImage, rental_order.startTime, rental_order.endTime, rental_order.actualStartTime, rental_order.actualReturnTime, rental_order.vehicleDailyPrice, rental_order.totalDays, rental_order.vehicleTotalAmount, rental_order.needDriver, rental_order.driverId, driver.driverName, rental_order.driverDailyPrice, rental_order.driverTotalAmount, rental_order.totalAmount, rental_order.status, rental_order.paymentStatus, rental_order.paymentTime, rental_order.refundAmount, rental_order.refundTime, rental_order.pickupStoreId, pickup_store.storeName AS pickupStoreName, rental_order.returnStoreId, return_store.storeName AS returnStoreName, rental_order.cancelReason, rental_order.cancelTime, rental_order.expireTime, rental_order.remark, rental_order.createTime FROM rental_order INNER JOIN user ON rental_order.userId = user.id AND user.isDelete = 0 INNER JOIN vehicle ON rental_order.vehicleId = vehicle.id AND vehicle.isDelete = 0 LEFT JOIN driver ON rental_order.driverId = driver.id AND driver.isDelete = 0 INNER JOIN store pickup_store ON rental_order.pickupStoreId = pickup_store.id AND pickup_store.isDelete = 0 INNER JOIN store return_store ON rental_order.returnStoreId = return_store.id AND return_store.isDelete = 0 WHERE rental_order.isDelete = 0
AND rental_order.userId = #{userId}
AND rental_order.status = #{req.status}
AND ( user.userName LIKE CONCAT('%', #{req.searchText}, '%') OR vehicle.name LIKE CONCAT('%', #{req.searchText}, '%') OR driver.driverName LIKE CONCAT('%', #{req.searchText}, '%') OR pickup_store.storeName LIKE CONCAT('%', #{req.searchText}, '%') return_store.storeName LIKE CONCAT('%', #{req.searchText}, '%') )
order by rental_order.${req.sortField}
desc

SELECT rental_order.id, rental_order.orderNo, rental_order.userId, user.userName AS userName, rental_order.vehicleId, vehicle.name AS vehicleName, vehicle.imageUrl AS vehicleImage, rental_order.startTime, rental_order.endTime, rental_order.actualStartTime, rental_order.actualReturnTime, rental_order.vehicleDailyPrice, rental_order.totalDays, rental_order.vehicleTotalAmount, rental_order.needDriver, rental_order.driverId, driver.driverName, rental_order.driverDailyPrice, rental_order.driverTotalAmount, rental_order.totalAmount, rental_order.status, rental_order.paymentStatus, rental_order.paymentTime, rental_order.refundAmount, rental_order.refundTime, rental_order.pickupStoreId, pickup_store.storeName AS pickupStoreName, rental_order.returnStoreId, return_store.storeName AS returnStoreName, rental_order.cancelReason, rental_order.cancelTime, rental_order.expireTime, rental_order.remark, rental_order.createTime FROM rental_order INNER JOIN user ON rental_order.userId = user.id AND user.isDelete = 0 INNER JOIN vehicle ON rental_order.vehicleId = vehicle.id AND vehicle.isDelete = 0 LEFT JOIN driver ON rental_order.driverId = driver.id AND driver.isDelete = 0 INNER JOIN store pickup_store ON rental_order.pickupStoreId = pickup_store.id AND pickup_store.isDelete = 0 INNER JOIN store return_store ON rental_order.returnStoreId = return_store.id AND return_store.isDelete = 0 WHERE rental_order.isDelete = 0
AND rental_order.orderNo LIKE CONCAT('%', #{req.orderNo}, '%')
AND rental_order.userId = #{req.userId}
AND user.userName LIKE CONCAT('%', #{req.userName}, '%')
AND rental_order.vehicleId = #{req.vehicleId}
AND vehicle.name LIKE CONCAT('%', #{req.vehicleName}, '%')
AND rental_order.startTime >= #{req.startTime}
AND rental_order.endTime <= #{req.endTime}
AND rental_order.actualStartTime >= #{req.actualStartTime}
AND rental_order.actualReturnTime <= #{req.actualReturnTime}
AND rental_order.totalDays = #{req.totalDays}
AND rental_order.needDriver = #{req.needDriver}
AND rental_order.status = #{req.status}
AND rental_order.paymentStatus = #{req.paymentStatus}
AND rental_order.pickupStoreId = #{req.pickupStoreId}
AND rental_order.returnStoreId = #{req.returnStoreId}
order by rental_order.${req.sortField}
desc
